import react, {useState, useEffect} from "react"
import axios from "axios"
import Country from "./components/Country"
import './App.css';

function App() {

  const [countries,setCountries]=useState([])
  const [searchcountry,setSearchcountry]=useState('')

  const hook = ()=>{
    axios.get("https://restcountries.eu/rest/v2/all").then(response=>{
        setCountries(response.data)
    })
  }

  useEffect(hook,[])

  const filterHandler=(e)=>{
    setSearchcountry(e.target.value)
    if (e.target.value.length===1) {
      window.alert("Too many matches,specifiy another filter")
    }
  }

  const showcountries = searchcountry.length===0?countries:countries.filter(country=>{return country.name.includes(searchcountry)})

  return (
    <div>
      <div>
        <p>find countries:</p><input value={searchcountry} onChange={filterHandler}></input>
      </div>
      <div>
        {showcountries.map((country)=><Country props={country}/>)}
      </div>
    </div>
  );
}

export default App;
