import axios from "axios"
import react, {useState, useEffect} from "react"

const CountryInfo = ({country})=>{

    console.log(country.capital)

    const [weather,setWeather]=useState([])

    useEffect(()=>{
    const fetchData= async ()=>{
        const result=await axios(`http://api.weatherstack.com/current?access_key=0bcf1cc82c6ef26d96f0b36d146c06db&query=Beijing
        `);
        console.log(result)
        setWeather(result.data);
    };
    fetchData();    
    },[])

    console.log(weather)

    return (           
        <div id="country">
        <h2>{country.name}</h2>
        <p>capital: {country.capital}</p>
        <p>population: {country.population}</p>
        <b>languages:</b>
        <ul>{country.languages.map((language)=>{return <li>{language.name}</li>})}</ul>
        <figure>
            <img src={country.flag} height="10%" width="10%"/>
        </figure>
        {weather && <div>
        <h2>Weather in {country.capital}</h2>
        <b>temperature:</b><p>{weather.current.temperature} Celcius</p>
        <figure>
            <img src={weather.current.weather_icons[0]} height="10%" width="10%"/>
        </figure>
        <b>wind:</b><p>{weather.current.wind_speed} mph direction {weather.current.wind_dir}</p>
        </div>}
        </div>)
}

export default CountryInfo;
