import react, {useState} from "react"
import Country from "./Country"

const Countries = ({countriesShow})=>{

    const [show,setShow]=useState(false)

    if(countriesShow.length===1){
        const country=countriesShow[0]
        return (<Country country={country}/>)
    }
    else {
        return (countriesShow.map((country)=>
        show?<Country country={country}/>:<div><p>{country.name}</p><button onClick={()=>setShow(true)}>show</button></div>    
        ))
    }
}

export default Countries;
