import react, {useState, useEffect} from "react"
import axios from "axios"
import Country from "./components/Country"
import './App.css';

function App() {

  const [countries,setCountries]=useState([])
  const [search,setSearch]=useState('')

  const hook = ()=>{
    axios.get("https://restcountries.eu/rest/v2/all").then(response=>{
        setCountries(response.data)
    })
  }
  useEffect(hook,[])

  // countries.forEach(function (element) {
  //   element.show = false;
  // });

  const filterHandler=(e)=>{
    setSearch(e.target.value)
  }

  const countriesShow = countries.filter(country=>country.name.toLowerCase().includes(search.toLowerCase()))

  return (
    <div>
      <div>
        <p>find countries:</p><input value={search} onChange={filterHandler}></input>
      </div>
      <div>
        {
          search.length===0?
          (countries.map(country=><p>{country.name}</p>)):
          (countriesShow.length>10?
            (<p>Too many matches, choose another filter!</p>):
            (countriesShow.map(country=><Country country={country}/>)))
        }
      </div>
    </div>
  );
}

export default App;
