import react, {useState, useEffect} from "react"
import axios from "axios"
import './App.css';

function App() {

  const [countries,setCountries]=useState([])
  const [searchcountry,setSearchcountry]=useState(' ')
  const [showcountries,setShowcountries]=useState(countries)

  const hook = ()=>{
    axios.get("https://restcountries.eu/rest/v2/all").then(response=>{
        setCountries(response.data)
    })
  }

  useEffect(hook,[])

  const filterHandler=(e)=>{
      setSearchcountry(e.target.value)
      if (searchcountry.length===0){
        setShowcountries(countries)
      }
      if (searchcountry.length===1){
        window.alert("Too many matches, specifiy another filter")
        return
      }
      if (searchcountry.length>1){
          setShowcountries(countries.filter(country=>(country.name.includes(searchcountry))))
      }

  }

  return (
    <div>
      <div>
        <p>find countries:</p><input value={searchcountry} onChange={filterHandler}></input>
      </div>
      <div>
        {showcountries.map(country=><p>{country.name}</p>)}
      </div>
    </div>
  );
}

export default App;
