import react, {useState} from "react"
import Country from "./CountryInfo"

const Countries = ({countriesShow})=>{

    if(countriesShow.length===1){
        const country=countriesShow[0]
        return (<CountryInfo country={country}/>)
    }
    else {
        return (countriesShow.map((country)=>{
        const [show,setShow]=useState(false)
        return (show?<CountryInfo country={country}/>:<div><p>{country.name}</p><button onClick={()=>setShow(!show)}>show</button></div>)
        }   
        ))
    }
}

export default Countries;
