import react, {useState,useEffect} from "react"

const CountryInfo = ({country})=>{

    const [weather,setWeather]=useState([])

    const hook = ()=>{
    axios.get(`http://api.weatherstack.com/current?access_key=0bcf1cc82c6ef26d96f0b36d146c06db&query=${country.name}`)
    .then(response=>{
        setWeather(response.data)
    })
  }
  useEffect(hook,[])
  console.log(weather)

    return (           
        <div id="country">
        <h2>{country.name}</h2>
        <p>capital: {country.capital}</p>
        <p>population: {country.population}</p>
        <b>languages:</b>
        <ul>{country.languages.map((language)=>{return <li>{language.name}</li>})}</ul>
        <figure>
            <img src={country.flag} height="10%" width="10%"/>
        </figure>
        </div>)
}

export default CountryInfo;
