import react from "react"
import Country from "./Country"

const Countries = ({countries,search})=>{
    console.log(countries)
    console.log(search)
    if (search.length===0){
        return (countries.map((country,i)=>{return <Country props={country} key={i}/>}))
    }
    else if (search.length===1){
        window.alert("Too many matches,specifiy another filter")
        return ('')
    }
    else {
        const filter_countries=countries.filter(country=>{country.name.includes(search)})
        console.log(filter_countries)
        if (filter_countries.length===0){
            return ('')
        }
        else if(filter_countries.length===1){
            return (<p>{filter_countries}</p>)
        }
        else if (filter_countries.length>1){
            return (filter_countries.map((country,i)=>{return <Country props={country} key={i}/>}))
        }
    }
}

export default Countries;
