import react, {useState, useEffect} from "react"
import axios from "axios"
import Country from "./components/Country"
import Countries from "./components/Countries"
import './App.css';

function App() {

  const [countries,setCountries]=useState([])
  const [search,setSearch]=useState('')
  const [show,setShow]=useState({})

  const hook = ()=>{
    axios.get("https://restcountries.eu/rest/v2/all").then(response=>{
        setCountries(response.data)
    })
  }
  useEffect(hook,[])

  // countries.forEach(function (element) {
  //   element.show = false;
  // });

  const filterHandler=(e)=>{
    setCountries(countries.filter(countries.name.includes(e.target.value.toLowerCase())))
  }

  return (
    <div>
      <div>
        <p>find countries:</p><input value={search} onChange={filterHandler}></input>
      </div>
      <div>
        {
        countries.length===0? countries.map((country)=><p>{country.name}</p>):
        (countries.length>10? <p>Too many matches!</p>: <p> </p> )
        }
      </div>
    </div>
  );
}

export default App;
