import react from "react"
import Country from "./Country"

const countryShow=(show)=>{
    return show?<Country country={country}/>:<div><p>{country.name}</p><button onClick={()=>{country.show=true}}>show</button></div>
}

const Countries = ({countries,search})=>{
    if (search.length===0){
        return (countries.map((country,i)=>{return <p>{country.name}</p>}))
    }
    else if (search.length===1){
        window.alert("Too many matches,specifiy another filter")
        return ('')
    }
    else {
        const filter_countries=countries.filter(country=>{return country.name.toLowerCase().includes(search.toLowerCase())})
        if (filter_countries.length===0){
            return ('')
        }
        else if(filter_countries.length===1){
            const country=filter_countries[0]
            return (<Country country={country}/>)
        }
        else if (filter_countries.length>1){
            return (filter_countries.map((country,i)=>{
            <div><p>{country.name}</p><button onClick={()=>{countryShow(true)}}>show</button></div>}
            ))
        }
    }
}

export default Countries;
