import react from "react"
import Country from "./Country"

const Countries = ({countries,search})=>{
    if (search.length===0){
        return (countries.map((country,i)=>{return <Country props={country} key={i}/>}))
    }
    else if (search.length===1){
        window.alert("Too many matches,specifiy another filter")
        return ('')
    }
    else {
        const filter_countries=countries.filter(country=>{return country.name.toLowerCase().includes(search.toLowerCase())})
        if (filter_countries.length===0){
            return ('')
        }
        else if(filter_countries.length===1){
            const country=filter_countries[0]
            console.log(country)
            return (
            <div>
            <h2>{country.name}</h2>
            <p>capital {country.capital}</p>
            <p>population {country.population}</p>
            <h3>languages:</h3>
            <ul>{country.languages.map((language)=>{return <li>{language.name}</li>})}</ul>
            <figure>
                <img src={country.flag} height="10%" width="10%"/>
            </figure>
            </div>
            )
        }
        else if (filter_countries.length>1){
            return (filter_countries.map((country,i)=>{return <Country props={country} key={i}/>}))
        }
    }
}

export default Countries;
