import React, { useState } from 'react'
import Person from './components/Person'

const App=()=>{

  const [persons,setPersons]=useState([
    {name:'Arto Hellas',
    number:'040-1234567',
    show:true}
  ])

  const [newname,setName]=useState('')
  const [newnumber,setNumber]=useState('')
  const [filtername,setFilter]=useState('')
  const [showname,setShowname]=useState({})
  const [show,setShow]=useState(true)

  const submitHandler = (props)=>{
    props.preventDefault()
    if (persons.filter(person=>( person.name===newname)).length>0){
      window.alert(`${newname} is already added to phonebook`)
      return
    }
    const personObject = {
      name:newname,
      number:newnumber,
      show:true
    }
    setPersons(persons.concat(personObject))
    setName("")
  }

  const clickHandler1 = (props) =>{
    setName(props.target.value)
  }

  const clickHandler2 = (props) =>{
    setNumber(props.target.value)
  }



  const clickHandler3 = (props) =>{
    setFilter(props.target.value)
    setShowname(persons.filter(person=>person.name.includes(filtername)).length>0?persons.filter(person=>person.name.includes(filtername)):{})
  }

  return(
    <div>
    <h2>Phonebook</h2>
    <div>filter shown with <input onChange={clickHandler3}></input></div>
    <h2>add a new</h2>
      <form onSubmit={submitHandler}>
        <div>name:<input value={newname} onChange={clickHandler1}></input></div>
        <div>number:<input value={newnumber} onChange={clickHandler2}></input></div>
        <div><button type="submit">add</button></div>
      </form>
      <h2>Numbers</h2>
      {showname.map((person,i)=>{return <Person props={person} key={i}/>})}
    </div>
  )
}

export default App
