import React from 'react'

const PersonForm = ({newname,nameHandler,newnumber,numberHandler,formHandler})=>{
    return(      
        <form onSubmit={formHandler}>
        <div>name:<input value={newname} onChange={nameHandler}></input></div>
        <div>number:<input value={newnumber} onChange={numberHandler}></input></div>
        <div><button type="submit">add</button></div>
        </form>
    )
}

export default PersonForm
