import React, { useState } from 'react'
import Number from './components/Number'

const App = () => {
  const [ persons, setPersons ] = useState([
    { name: 'Arto Hellas' }
  ]) 
  const [ newName, setNewName ] = useState('')

  const addPerson=(event)=>{
    event.preventDefault()
    console.log(Object.values(persons))
    console.log(persons.includes({name:newName}))
    if (Object.values(persons).indexOf(newName)>-1){
      alert(`${newName} is already added to phonebook`);
    }
    setPersons(persons.concat({name:newName}))
    setNewName('')
  }

  const clickHandler=(event)=>{
    setNewName(event.target.value)
  }

  return (
    <div>
      <h2>Phonebook</h2>
      <form onSubmit={addPerson}>
        <div>
          name: <input value={newName} onChange={clickHandler}/>
        </div>
        <div>
          <button type="submit">add</button>
        </div>
      </form>
      <h2>Numbers</h2>
      <div>
        {persons.map((person,i)=><Number props={person} key={i}/>)}
      </div>
    </div>
  )
}

export default App
