import React, { useState } from 'react'
import Person from './components/Person'

const App=()=>{

  const [persons,setPersons]=useState([
    {name:'Arto Hellas',
    number:'040-1234567'}
  ])

  const [newname,setName]=useState('')

  const [newnumber,setNumber]=useState('')

  const submitHandler = (props)=>{
    props.preventDefault()
    if (persons.filter(person=>( person.name===newname)).length>0){
      window.alert(`${newname} is already added to phonebook`)
      return
    }
    const personObject = {
      name:newname,
      number:newnumber
    }
    setPersons(persons.concat(personObject))
    setName("")
  }

  const clickHandler = (props) =>{
      setName(props.target.value)
  }

  const clickHandler_ = (props) =>{
    setNumber(props.target.value)
}

  return(
    <div>
    <h2>Phonebook</h2>
      <form onSubmit={submitHandler}>
        <div>name:<input value={newname} onChange={clickHandler}></input></div>
        <div>number:<input value={newnumber} onChange={clickHandler_}></input></div>
        <div><button type="submit">add</button></div>
      </form>
      <h2>Numbers</h2>
      {persons.map((person,i)=>{return <Person props={person} key={i}/>})}
    </div>
  )
}

export default App
