import React, { useState } from 'react'
import Person from './components/Person'
import Filter from './components/Filter'
import PersonForm from './components/PersonForm'

const App=()=>{

  const [persons,setPersons]=useState([
    {name:'Arto Hellas',
    number:'040-1234567'}
  ])

  const [newname,setName]=useState('')
  const [newnumber,setNumber]=useState('')
  const [searchname,setSearch]=useState('')
  // const [show,setShow]=useState(true)

  const formHandler = (props)=>{
    props.preventDefault()
    if (persons.filter(person=>( person.name===newname)).length>0){
      window.alert(`${newname} is already added to phonebook`)
      return
    }
    const personObject = {
      name:newname,
      number:newnumber,
    }
    setPersons(persons.concat(personObject))
    setName("")
  }

  const nameHandler = (props) =>{
    setName(props.target.value)
  }

  const numberHandler = (props) =>{
    setNumber(props.target.value)
  }

  const filterHandler = (props) =>{
    setSearch(props.target.value)
  }

  const showpersons=persons.filter(person=>(person.name.includes(searchname)))

  return(
    <div>
    <h2>Phonebook</h2>
    <Filter searchname={searchname} filterHandler={filterHandler}/>
    <h3>add a new</h3>
    <PersonForm newname={newname} nameHandler={nameHandler} newnumber={newnumber} numberHandler={numberHandler} formHandler={formHandler}/>
    <h2>Numbers</h2>
    {showpersons.map((person,i)=>{return <Person props={person} key={i}/>})}
    </div>
  )
}

export default App
